import PropTypes from 'prop-types';
import React, { PureComponent } from 'react';
import Icon from 'Components/Icon';
import { icons } from 'Helpers/Props';
import styles from './HeartRating.css';

class HeartRating extends PureComponent {

  //
  // Render

  render() {

    const {
      ratings,
      hideIcon,
      iconSize
    } = this.props;

    const imdbImage = 'data:image/png;base64,';

    console.log(ratings);

    let rating = ratings.imdb;

    if (!rating) {
      rating = ratings.tmdb;
    }

    let ratingString = '0%';

    if (rating) {
      ratingString = ratings.imdb ? `${rating.value}` : `${rating.value * 10}%`;
    }

    return (
      <span>
        {
          !hideIcon && !ratings.imdb ?
            <Icon
              className={styles.heart}
              name={icons.HEART}
              size={iconSize}
            /> :
            <img
              className={styles.imdb}
              src={imdbImage}
            />
        }

        {ratingString}
      </span>
    );
  }
}

HeartRating.propTypes = {
  ratings: PropTypes.object.isRequired,
  iconSize: PropTypes.number.isRequired,
  hideIcon: PropTypes.bool
};

HeartRating.defaultProps = {
  iconSize: 14
};

export default HeartRating;
